select movie_info.info AS info, title.id AS title_id_1, movie_keyword.movie_id AS movie_keyword_movie_id_2, keyword.id AS keyword_id_3, keyword.keyword AS keyword, movie_info.movie_id AS movie_info_movie_id_5, title.production_year AS production_year, title.title AS title
 from movie_keyword,movie_info,keyword,title
 where title.id = movie_keyword.movie_id  And  movie_keyword.movie_id = movie_info.movie_id And (keyword.id = movie_keyword.keyword_id) And ((movie_info.info = 'Sweden') Or (movie_info.info =  'Norway') Or (movie_info.info =  'Germany') Or (movie_info.info =  'Denmark') Or (movie_info.info =  'Swedish') Or (movie_info.info =  'Denish') Or (movie_info.info =  'Norwegian') Or (movie_info.info =  'German') Or (movie_info.info =  'USA') Or (movie_info.info =  'American')) And title.id = movie_info.movie_id  And ((keyword.keyword like '%sequel%')) And (title.production_year > 1990)