select movie_info.info AS info, title.id AS title_id_1, movie_info.movie_id AS movie_info_movie_id_2, movie_companies.company_type_id AS company_type_id, info_type.id AS info_type_id_4, title.production_year AS production_year, title.title AS title, movie_companies.note AS note, movie_companies.movie_id AS movie_companies_movie_id_8, company_type.kind AS kind
 from title,info_type,company_type,movie_info,movie_companies
 where (title.production_year > 1990) And title.id = movie_info.movie_id  And  title.id = movie_companies.movie_id And (info_type.id = movie_info.info_type_id) And (company_type.kind = 'production companies') And (company_type.id = movie_companies.company_type_id) And ((movie_info.info = 'USA') Or (movie_info.info =  'America')) And  movie_companies.movie_id = movie_info.movie_id And (movie_companies.note like '%(VHS)%')  And  (movie_companies.note like '%(USA)%')  And  (movie_companies.note like '%(1994)%')