select movie_info.info AS movie_info_info_0, movie_info_idx.info AS movie_info_idx_info_1, company_name.country_code AS country_code, info_type.info AS info_type_info_3, company_name.id AS company_name_id_4, movie_info.movie_id AS movie_info_movie_id_5, movie_companies.company_type_id AS company_type_id, movie_info_idx.movie_id AS movie_info_idx_movie_id_7, info_type.id AS info_type_id_8, company_name.name AS name, movie_info.info_type_id AS info_type_id, movie_companies.movie_id AS movie_companies_movie_id_11, company_type.kind AS kind
 from movie_info_idx,info_type,company_type,company_name,movie_info,movie_companies
 where movie_companies.movie_id = movie_info_idx.movie_id  And  movie_info.movie_id = movie_info_idx.movie_id And (movie_info_idx.info_type_id = info_type.id) And (movie_info_idx.info > '7.0') And  movie_info.info_type_id = info_type.id And (company_type.id = movie_companies.company_type_id) And (company_type.kind = 'production companies') And (company_name.id = movie_companies.company_id) And (company_name.country_code = '[us]') And movie_companies.movie_id = movie_info.movie_id  And ((movie_info.info = 'Drama') Or (movie_info.info =  'Horror') Or (movie_info.info =  'Western') Or (movie_info.info =  'Family'))