create materialized view if not exists mv106 as select cast_info.movie_id AS movie_id, cast_info.note AS note, cast_info.person_id AS person_id, cast_info.person_role_id AS person_role_id, cast_info.role_id AS role_id, char_name.name AS char_name_name_5, movie_companies.company_id AS company_id, name.gender AS gender, name.name AS name_name_8, role_type.role AS role
 from cast_info,name,movie_companies,role_type,char_name
 where (movie_companies.movie_id = cast_info.movie_id) And (cast_info.role_id = role_type.id) And (cast_info.note in ('(voice)', '(voice: Japanese version)', '(voice) (uncredited)', '(voice: English version)')) And (name.id = cast_info.person_id) And (char_name.id = cast_info.person_role_id) And (role_type.role = 'actress')