create materialized view if not exists mv218 as select info_type.id AS id, info_type.info AS info, movie_info.movie_id AS movie_id, movie_info.note AS note, title.production_year AS production_year, title.title AS title
 from title,movie_info,info_type
 where (title.id = movie_info.movie_id) And (info_type.id = movie_info.info_type_id) And (movie_info.note like '%internet%') And (info_type.info = 'release dates')