create materialized view if not exists mv317 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS comp_cast_type_kind_1, complete_cast.movie_id AS complete_cast_movie_id_2, complete_cast.status_id AS status_id, keyword.id AS keyword_id_4, keyword.keyword AS keyword, kind_type.id AS kind_type_id_6, kind_type.kind AS kind_type_kind_7, movie_info_idx.info AS info, movie_info_idx.info_type_id AS info_type_id, movie_info_idx.movie_id AS movie_info_idx_movie_id_10, title.production_year AS production_year, title.title AS title
 from title,comp_cast_type,movie_keyword,keyword,kind_type,complete_cast,movie_info_idx
 where (title.id = movie_keyword.movie_id) And (movie_info_idx.movie_id = title.id) And (kind_type.id = title.kind_id) And (complete_cast.subject_id = comp_cast_type.id) And (comp_cast_type.kind = 'cast') And (complete_cast.movie_id = movie_keyword.movie_id) And (movie_keyword.keyword_id = keyword.id) And (keyword.keyword in ('superhero', 'marvel-comics', 'based-on-comic', 'tv-special', 'fight', 'violence', 'magnet', 'web', 'claw', 'laser')) And (movie_info_idx.info > '7.0')