create materialized view if not exists mv347 as select comp_cast_type.id AS comp_cast_type_id_0, comp_cast_type.kind AS comp_cast_type_kind_1, company_name.country_code AS country_code, company_name.id AS company_name_id_3, company_name.name AS name, company_type.id AS company_type_id_5, company_type.kind AS company_type_kind_6, complete_cast.movie_id AS movie_id, complete_cast.subject_id AS subject_id, movie_companies.note AS note
 from company_type,comp_cast_type,movie_companies,company_name,complete_cast
 where (movie_companies.company_type_id = company_type.id) And (company_type.kind = 'production companies') And (complete_cast.status_id = comp_cast_type.id) And (comp_cast_type.kind = 'complete') And (movie_companies.movie_id = complete_cast.movie_id) And (movie_companies.note IS NULL) And (company_name.id = movie_companies.company_id)