create materialized view if not exists mv36 as select company_type.id AS id, company_type.kind AS kind, movie_companies.movie_id AS movie_id, movie_companies.note AS note, movie_info.info AS info, movie_info.info_type_id AS info_type_id
 from company_type,movie_info,movie_companies
 where (company_type.id = movie_companies.company_type_id) And (company_type.kind = 'production companies') And (movie_info.movie_id = movie_companies.movie_id) And (movie_info.info in ('USA', 'America')) And (movie_companies.note like '%(VHS)%')  And  (movie_companies.note like '%(USA)%')  And  (movie_companies.note like '%(1994)%')