select keyword.id AS keyword_id_0, keyword.keyword AS keyword, link_type.id AS link_type_id_2, link_type.link AS link, movie_keyword.movie_id AS movie_id
 from link_type,keyword,movie_link,movie_keyword
 where (movie_link.link_type_id = link_type.id) And (link_type.link like '%follow%') And (movie_keyword.keyword_id = keyword.id) And (keyword.keyword = 'sequel') And (movie_keyword.movie_id = movie_link.movie_id)