select info_type.id AS id, info_type.info AS info_type_info_1, movie_companies.company_id AS company_id, movie_companies.movie_id AS movie_id, movie_companies.note AS note, movie_info.info AS movie_info_info_5, title.production_year AS production_year, title.title AS title
 from movie_info,info_type,title,movie_companies
 where (info_type.id = movie_info.info_type_id) And (movie_info.info IS NOT NULL)  And  (((movie_info.info = '__LIKE__Japan:%2007%')) Or ((movie_info.info = '__LIKE__USA:%2008%'))) And (title.id = movie_info.movie_id) And (info_type.info = 'release dates') And (title.id = movie_companies.movie_id) And (movie_companies.note like '%(200%)%')  And  (((movie_companies.note like '%(USA)%')) Or ((movie_companies.note like '%(worldwide)%')))