select aka_name.name AS name, aka_name.person_id AS person_id, cast_info.movie_id AS movie_id, info_type.id AS info_type_id_3, info_type.info AS info, link_type.id AS link_type_id_5, link_type.link AS link, person_info.info_type_id AS info_type_id, person_info.note AS note, title.production_year AS production_year, title.title AS title
 from aka_name,link_type,info_type,cast_info,title,person_info,movie_link
 where (person_info.person_id = aka_name.person_id) And (aka_name.name like '%a%') And (movie_link.link_type_id = link_type.id) And (link_type.link = 'features') And (info_type.id = person_info.info_type_id) And (info_type.info = 'mini biography') And (person_info.person_id = cast_info.person_id) And (cast_info.movie_id = title.id) And (title.id = movie_link.linked_movie_id) And (person_info.note = 'Volker Boehm')