select aka_name.name AS aka_name_name_0, aka_name.person_id AS person_id, cast_info.movie_id AS cast_info_movie_id_2, cast_info.note AS cast_info_note_3, company_name.country_code AS country_code, company_name.id AS company_name_id_5, movie_companies.movie_id AS movie_companies_movie_id_6, movie_companies.note AS movie_companies_note_7, name.name AS name_name_8, role_type.id AS role_type_id_9, role_type.role AS role, title.title AS title
 from aka_name,cast_info,title,name,movie_companies,role_type,company_name
 where (aka_name.person_id = name.id) And (title.id = cast_info.movie_id) And (movie_companies.movie_id = cast_info.movie_id) And (cast_info.role_id = role_type.id) And (cast_info.note = '(voice: English version)') And (name.id = cast_info.person_id) And (movie_companies.company_id = company_name.id) And (movie_companies.note like '%(Japan)%')  And  (movie_companies.note not like '%(USA)%') And (role_type.role = 'actress') And (company_name.country_code = '[jp]')